import React, { useState } from "react";
import { getSearch, useNowState, useNowDispatch } from "./MoviesContext";
import styled from "styled-components";

const InsertForm = styled.form``;
const Input = styled.input`
  width: 100%;

  padding: 10px;

  font-size: 16px;
`;

const Search = () => {
  const [keyword, setKeyword] = useState("");

  const onChange = ({ target }) => setKeyword(target.value);

  const state = useNowState();
  const dispatch = useNowDispatch();

  const { data: topList, loading, error } = state.search;

  const onSubmit = (e) => {
    e.preventDefault();
    getSearch(dispatch, keyword);
    setKeyword("");
  };

  return (
    <div>
      <h1>Search</h1>
      <InsertForm onSubmit={onSubmit}>
        <Input placeholder="Search" onChange={onChange} autoFocus />
      </InsertForm>
      {/* {searchList ? "검색중" : null} */}
    </div>
  );
};

export default Search;
