import React, { createContext, useReducer, useContext } from "react";
import { movies } from "./api";
import createAsyncDispatcher, {
  initialAsyncState,
  createAsyncHandler,
} from "./asyncActionUtils";

const initialState = {
  now: initialAsyncState,
};
const nowHandler = createAsyncHandler("GET_NOW", "now");

function nowReducer(state, action) {
  switch (action.type) {
    case "GET_NOW":
    case "GET_NOW_SUCCESS":
    case "GET_NOW_ERROR":
      return nowHandler(state, action);
    default:
      throw new Error("Unhandled action type", action.type);
  }
}

const NowStateContext = createContext(null);
const NowDispatchContext = createContext(null);

export function UsersProvider({ children }) {
  const [state, dispatch] = useReducer(nowReducer, initialState);
  return (
    <NowStateContext.Provider value={state}>
      <NowDispatchContext.Provider value={dispatch}>
        {children}
      </NowDispatchContext.Provider>
    </NowStateContext.Provider>
  );
}

export function useNowState() {
  const state = useContext(NowStateContext);
  if (!state) {
    throw new Error("Cannot find NowProvider");
  }
  return state;
}

export function useNowDispatch() {
  const dispatch = useContext(NowDispatchContext);
  if (!dispatch) {
    throw new Error("Cannot find NowProvider");
  }
  return dispatch;
}

export const getNow = createAsyncDispatcher("GET_NOW", movies.getNowPlaying);

export async function getNowPlaying() {
  const response = await movies.getNowPlaying();
  return response;
}

export const MoviesContext = () => {
  return <div></div>;
};
