import React, { useEffect, useState } from "react";
import { useNowState, useNowDispatch, getNow } from "./MoviesContext";
import { Link } from "react-router-dom";
import styled from "styled-components";
import SubRouter from "./Router/SubRouter";

const MovieList = styled.ul`
  display: flex;
  flex-wrap: wrap;
`;

const MovieItem = styled.li`
  width: calc(25% - 20px);

  padding: 10px;
  margin: 10px;

  text-align: center;

  cursor: pointer;

  img {
    width: 100%;
  }
`;

const Movies = () => {
  useEffect(() => {
    fetchData();
  }, []);

  const state = useNowState();
  const dispatch = useNowDispatch();

  const { data: movieList, loading, error } = state.now;
  const fetchData = () => {
    getNow(dispatch);
  };

  if (loading) return <div>로딩중..</div>;
  if (error) return <div>에러.</div>;
  if (!movieList) return null;

  // console.log(movieList);

  const [onDetailView, setOnDetailView] = useState(false);

  return (
    <>
      <h1>Now Playing</h1>
      <MovieList>
        {movieList.map(({ id, title, poster_path }) => (
          <>
            <MovieItem id={id} key={id}>
              <Link to={"/now/" + id} key={`link` + id}>
                <strong>{title}</strong>
                <img
                  className="movie_img"
                  src={`https://image.tmdb.org/t/p/w500/` + poster_path}
                  alt={title}
                />
              </Link>
            </MovieItem>
          </>
        ))}
        <SubRouter />
      </MovieList>
    </>
  );
};

export default Movies;
