import React, { useEffect } from "react";
import {
  useNowState,
  useNowDispatch,
  getNow,
  getUp,
  getTop,
} from "./MoviesContext";
import { Link } from "react-router-dom";
import styled from "styled-components";

const MainTopList = styled.ul`
  display: flex;
  flex-wrap: nowrap;
`;

const MainTopItem = styled.li`
  width: calc(25% - 20px);

  padding: 10px;
  margin: 10px;

  text-align: center;

  cursor: pointer;

  img {
    width: 100%;
  }
`;

const Main = () => {
  useEffect(() => {
    fetchData();
  }, []);

  const state = useNowState();
  const dispatch = useNowDispatch();

  const { data: topList, loading, error } = state.top;

  const fetchData = () => {
    getTop(dispatch);
  };

  if (loading) return <div>로딩중..</div>;
  if (error) return <div>에러.</div>;
  if (!topList) return null;

  return (
    <>
      <section>
        <h2>Top Box Office</h2>
        <MainTopList>
          {topList.map(({ id, title, poster_path }) => (
            <>
              <MainTopItem id={id} key={id}>
                <Link to={"/now/" + id} key={`link` + id}>
                  {/* <strong>{title}</strong> */}
                  <img
                    className="movie_img"
                    src={`https://image.tmdb.org/t/p/w500/` + poster_path}
                    alt={title}
                  />
                </Link>
              </MainTopItem>
            </>
          ))}
        </MainTopList>
      </section>
    </>
  );
};

export default Main;
