import React, { useEffect } from "react";
import { useNowState, useNowDispatch, getNow } from "./MoviesContext";
import styled from "styled-components";

const MovieListBlock = styled.div`
  width: 90%;

  margin: 0 auto;

  h1 {
    font-size: 14px;
    text-align: center;
  }
`;

const MovieList = styled.ul`
  display: flex;
  flex-wrap: wrap;
`;

const MovieItem = styled.li`
  width: calc(25% - 20px);

  padding: 10px;
  margin: 10px;
  text-align: center;

  img {
    width: 100%;
  }
`;

const Movies = () => {
  useEffect(() => {
    fetchData();
  }, []);

  const state = useNowState();
  const dispatch = useNowDispatch();

  const { data: movieList, loading, error } = state.now;
  const fetchData = () => {
    getNow(dispatch);
  };

  if (loading) return <div>로딩중..</div>;
  if (error) return <div>에러.</div>;
  if (!movieList) return null;

  console.log(movieList);

  return (
    <MovieListBlock>
      <h1>Now Playing</h1>
      <MovieList>
        {movieList.map(({ id, title, poster_path }) => (
          <MovieItem id={id} key={id}>
            <strong>{title}</strong>
            <img
              className="movie_img"
              src={`https://image.tmdb.org/t/p/w500/` + poster_path}
              alt={title}
            />
          </MovieItem>
        ))}
      </MovieList>
    </MovieListBlock>
  );
};

export default Movies;
