import React, { useEffect } from "react";
import { useNowState, useNowDispatch, getNow } from "./MoviesContext";
import styled from "styled-components";

const MovieListBlock = styled.div``;

const Movies = () => {
  useEffect(() => {
    fetchData();
  }, []);

  const state = useNowState();
  const dispatch = useNowDispatch();

  const { data: movieList, loading, error } = state.now;
  const fetchData = () => {
    getNow(dispatch);
  };

  if (loading) return <div>로딩중..</div>;
  if (error) return <div>에러.</div>;
  if (!movieList) return null;

  console.log(movieList);

  return (
    <MovieListBlock>
      <h1>Now Playing</h1>
      <ul>
        {movieList.map(({ id, title, original_title, poster_path }) => (
          <li id={id} key={id}>
            <strong>{title}</strong>
            {original_title}
            <img
              className="movie_img"
              src={`https://image.tmdb.org/t/p/w500/` + poster_path}
              alt={title}
            />
          </li>
        ))}
      </ul>
    </MovieListBlock>
  );
};

export default Movies;
