import React, { createContext, useReducer, useContext } from "react";
import { movies } from "./api";
import createAsyncDispatcher, {
  initialAsyncState,
  createAsyncHandler,
} from "./asyncActionUtils";

const initialState = {
  now: initialAsyncState,
  detail: initialAsyncState,
};
const nowHandler = createAsyncHandler("GET_NOW", "now");
const userHandler = createAsyncHandler("GET_DETAIL", "detail");

function usersReducer(state, action) {
  switch (action.type) {
    case "GET_USERS":
    case "GET_USERS_SUCCESS":
    case "GET_USERS_ERROR":
      return usersHandler(state, action);
    case "GET_USER":
    case "GET_USER_SUCCESS":
    case "GET_USER_ERROR":
      return userHandler(state, action);
    default:
      throw new Error("Unhandled action type", action.type);
  }
}

const UsersStateContext = createContext(null);
const UsersDispatchContext = createContext(null);

export function UsersProvider({ children }) {
  const [state, dispatch] = useReducer(usersReducer, initialState);
  return (
    <UsersStateContext.Provider value={state}>
      <UsersDispatchContext.Provider value={dispatch}>
        {children}
      </UsersDispatchContext.Provider>
    </UsersStateContext.Provider>
  );
}

export function useUsersState() {
  const state = useContext(UsersStateContext);
  if (!state) {
    throw new Error("Cannot find UserProvider");
  }
  return state;
}

export function useUsersDispatch() {
  const dispatch = useContext(UsersDispatchContext);
  if (!dispatch) {
    throw new Error("Cannot find UserProvider");
  }
  return dispatch;
}

export const getUsers = createAsyncDispatcher("GET_USERS", api.getUsers);
export const getUser = createAsyncDispatcher("GET_USER", api.getUser);

export async function getNowPlaying() {
  const response = await movies.getNowPlaying();
  return response;
}

export const MoviesContext = () => {
  return <div></div>;
};
