import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { getSearch, useNowState, useNowDispatch } from "./MoviesContext";
import styled from "styled-components";

const InsertForm = styled.form``;
const Input = styled.input`
  width: 100%;

  padding: 10px;

  font-size: 16px;
`;

const MovieList = styled.ul`
  display: flex;
  flex-wrap: wrap;
`;

const MovieItem = styled.li`
  width: calc(25% - 20px);

  padding: 10px;
  margin: 10px;

  text-align: center;

  cursor: pointer;

  img {
    width: 100%;
  }
`;

const Search = () => {
  const [keyword, setKeyword] = useState("");

  const initialState = [{ id: 1, text: "blue" }];
  const [keywordList, setKeywordList] = useState(initialState);

  const onChange = ({ target }) => setKeyword(target.value);

  const state = useNowState();
  const dispatch = useNowDispatch();
  // console.log(state.search);
  const { data: searchList, loading, error } = state.search;

  function onSubmit(e) {
    e.preventDefault();
    fetchData();

    setKeyword("");
  }

  async function fetchData() {
    try {
      await getSearch(dispatch, keyword);
      setKeywordList([...keywordList, keyword]);
    } catch (e) {
      throw new Error(`error: ${e}`);
    }
  }
  // console.log(keywordList);
  if (loading) return <div>로딩중..</div>;
  if (error) return <div>에러.</div>;
  // if (!searchList) return null;

  return (
    <div>
      <h1>Search</h1>
      <InsertForm onSubmit={onSubmit}>
        <Input placeholder="Search" onChange={onChange} autoFocus />
      </InsertForm>
      {keywordList ? <SearchHistory keywordList={keywordList} /> : null}
      {searchList ? <SearchList searchList={searchList} /> : null}
    </div>
  );
};

const SearchHistoryBlock = styled.ul`
  display: flex;
  flex-wrap: wrap;
`;

const KeywordItem = styled.li`
  padding: 10px 15px;
  margin: 10px 5px;

  background: #ccc;
  color: #fff;
  font-weight: bold;
  border-radius: 20px;
`;

const RemoveBtn = styled.div`
  display: inline-block;
  margin-left: 5px;
  font-weight: normal;
  color: #000;
  cursor: pointer;
`;

const SearchHistory = ({ keywordList }) => {
  // console.log(keywordList);
  return (
    <div>
      <SearchHistoryBlock>
        {keywordList.map((key) => (
          <KeywordItem>
            {key}
            <RemoveBtn>X</RemoveBtn>
          </KeywordItem>
        ))}
      </SearchHistoryBlock>
    </div>
  );
};

const SearchList = ({ searchList }) => {
  // console.log(searchList);
  return (
    <>
      <h1>search result</h1>
      <MovieList>
        {searchList.map(({ id, title, poster_path }) => (
          <>
            <MovieItem id={id} key={id}>
              <Link to={"/now/" + id} key={`link` + id}>
                <strong>{title}</strong>
                <img
                  className="movie_img"
                  src={`https://image.tmdb.org/t/p/w500/` + poster_path}
                  alt={title}
                />
              </Link>
            </MovieItem>
          </>
        ))}
      </MovieList>
    </>
  );
};

export default Search;
