import React, { useState } from "react";
import styled from "styled-components";
import { movies } from "./api";

const InsertForm = styled.form``;
const Input = styled.input``;

const Search = () => {
  const [keyword, setKeyword] = useState("");

  async function getSearch(dispatch, keyword) {
    dispatch({ type: "GET_SEARCH" });
    try {
      let term = keyword;
      const response = await movies.searchMovies(term);
      console.log(response);
    } catch (e) {
      dispatch({ type: "GET_SEARCH_ERROR", error: e });
    }
  }

  const onChange = ({ target }) => setKeyword(target.value);

  const onSubmit = (e) => {
    e.preventDefault();
    getSearch(keyword);
    setKeyword("");
  };

  return (
    <div>
      <h1>Search</h1>
      <InsertForm onSubmit={onSubmit}>
        <Input placeholder="Search" onChange={onChange} autoFocus />
      </InsertForm>
    </div>
  );
};

export default Search;
