import React, { useEffect } from "react";
import { getDetail, useNowState, useNowDispatch } from "./MoviesContext";

const Detail = ({ match }) => {
  // console.log(match);
  const { id } = match.params;

  // console.log(id);

  useEffect(() => {
    fetchData();
  }, []);

  const state = useNowState();
  const dispatch = useNowDispatch();

  const { data: movieList, loading, error } = state.now;
  const fetchData = () => {
    getDetail(dispatch, id);
  };

  if (loading) return <div>로딩중..</div>;
  if (error) return <div>에러.</div>;
  if (!movieList) return null;

  // console.log(movieList);
  return <div>디테일페이지입니다</div>;
};

export default Detail;
