  // popularity: 127.801
  // ​​
  // poster_path: "/7IiTTgloJzvGI1TAYymCfbfl3vT.jpg"
  // ​​
  // release_date: "2019-05-30"
  // ​​
  // title: "Parasite"
  // ​​
  // video: false
  // ​​
  // vote_average: 8.5
  // ​​
  // vote_count: 7794
  return (
    <div>
      <h1>Now Playing</h1>
      <ul>
        {movieList.map(({ id, title, original_title, poster_path }) => (
          <li id={id} key={id}>
            <strong>{title}</strong>
            {original_title}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Movies;
